"use strict";function openBurgerMenu(){document.getElementById("burger-menu");var e=Array.from(document.getElementsByClassName("burger-lines")),n=document.getElementById("burger-background"),t=document.getElementById("navigation");t.classList.contains("open")?(e.forEach((function(e,n){return e.classList.remove("open")})),n.classList.remove("open"),t.classList.remove("open")):(e.forEach((function(e,n){return e.classList.add("open")})),n.classList.add("open"),t.classList.add("open"))}document.getElementById("burger-menu").addEventListener("click",openBurgerMenu);